mapper.select:
SELECT *
FROM cultural_properties
WHERE 1=1

    AND category_name = #{searchForm.categoryName}


    AND region = #{searchForm.region}


    AND dynasty = #{searchForm.dynasty}


    AND cultural_properties_name = #{searchForm.culturalPropertiesName}

SELECT * FROM cultural_properties WHERE
category_name = #{category}
AND cultural_properties_name LIKE CONCAT('%', #{name}, '%')
AND region LIKE CONCAT('%', #{region}, '%')
AND dynasty LIKE CONCAT('%', #{dynasty}, '%')
LIMIT #{start}, #{end}

mapper.delete:
DELETE FROM cultural_properties

WHERE id IN

    #{id}
