mapper.select:
SELECT EXISTS (
SELECT 1
FROM cultural_properties_content_keyword
WHERE cultural_properties_id = #{culturalPropertiesId}
)

SELECT content
FROM cultural_properties_review
WHERE cultural_properties_id = #{culturalPropertiesId}

SELECT EXISTS (
SELECT 1
FROM cultural_properties_review_keyword
WHERE cultural_properties_id = #{culturalPropertiesId}
)

mapper.update:
UPDATE cultural_properties_review_keyword SET keyword = CASE id
WHEN #{item.id} THEN #{item.keyword}
END, updated_at = CURRENT_TIMESTAMP WHERE id IN
#{item.id}

